//字符串加密
export function secretPhone(value) {

    let string = value;
    if (isNotEmpty(value) && typeof (value) == 'string' && value.length == 11){
        var reg = /^(\d{3})\d*(\d{4})$/;
        string = value.replace(reg,'$1****$2');
    }

    return string;
}

//深拷贝对象
export function deepCopyObj(source) {
    var sourceCopy = source instanceof Array ? [] : {};
    for (var item in source) {
        sourceCopy[item] = typeof source[item] === 'object' ? deepCopyObj(source[item]) : source[item];
    }
    return sourceCopy;
}

