//判断是否弹出消息权限弹框逻辑
//一天一次，一周三次
export function alertLogic() {
    return new Promise((resolve, reject)=>{
        getItem(WeekNumber).then((id)=> {
            if (id){
                if (id != getWeekOfYear()){
                    setItem(WeekNumber,getWeekOfYear())
                    setItem(WeekDatas,[new Date().getDay()])
                    resolve(true)
                } else {
                    getItem(WeekDatas).then((temp)=>{
                        if (temp.length == 3){
                            resolve(false)
                        }  else {
                            let tempDay;
                            if (temp[temp.length-1] != new Date().getDay()){
                                tempDay = new Date().getDay();
                            }
                            if (tempDay){
                                temp.push(tempDay)
                                setItem(WeekDatas,temp);
                                resolve(true)
                            } else {
                                resolve(false)
                            }
                        }
                    });
                }
            } else{
                setItem(WeekNumber,getWeekOfYear())
                setItem(WeekDatas,[new Date().getDay()])
                resolve(true)
            }
        })
    })
}

/**
 *获取时间差值
 */
export function getTimeDifference(date){
    //差值
    let disDate = new Date() - new Date(date);

    //天
    let days=Math.floor(disDate/(24*3600*1000))
    //时
    let leave1=disDate%(24*3600*1000)
    let hours=Math.floor(disDate/(3600*1000))
    //分
    let leave2=leave1%(3600*1000)
    let minutes=Math.floor(disDate/(60*1000))
    //秒
    let leave3=leave2%(60*1000)
    let seconds=Math.round(disDate/1000)

    return seconds;
}

function getWeekOfYear(){
    let today = new Date();
    let firstDay = new Date(today.getFullYear(),0, 1);
    let dayOfWeek = firstDay.getDay();
    let spendDay= 1;
    if (dayOfWeek !=0) {
        spendDay=7-dayOfWeek+1;
    }
    firstDay = new Date(today.getFullYear(),0, 1+spendDay);
    let d =Math.ceil((today.valueOf()- firstDay.valueOf())/ 86400000);
    let result =Math.ceil(d/7);
    return result+1;
};

