/**
 * 更新导航栏购物车角标
 * tabBarcount: 请求返回购物车内商品数量,不填从本地数据取
 */
export function upadataTabBarCount(tabBarcount) {
  if (!isLogin()) {
    Taro.removeTabBarBadge({
      index: 2,
    })
    return
  }
  if (isEmpty(tabBarcount)){
    tabBarcount = Taro.getStorageSync('shopCarCount')
  }
  if (tabBarcount > 99) {
    tabBarcount = '99+'
  }
  if (tabBarcount == 0) {
    Taro.removeTabBarBadge({
      index: 2,
    })
  } else {
    Taro.setTabBarBadge({
      index: 2,
      text: tabBarcount.toString(),
    })
  }
}

export function sublie(val) {
    if (val == undefined || val.length == 0) {
        return;
    } else if (val.length > 0) {
      
        var arrs = val.split('').slice(0,-3).join('')
        return arrs
    }
}

export function subAfter(val) {
    if (val == undefined || val.length == 0) {
        return;
    } else if (val.length > 0) {
    
        var arrs = val.split('').slice(-2).join('')
        return arrs
    }
}
