/**
 * 保留两位小数点
 * @param x
 * @returns {*}
 */
export function toDecimal(x,type='up') {
  try {

    if (x == undefined) {
      return '0.00';
    }

    if (typeof (x) == 'string' && x.indexOf('-') != -1) {
      return x;
    }

    var f_x = parseFloat(x + "");
    if (isNaN(f_x)) {
      return '';
    }
    var f_x = type == 'up' ? Math.round(x * 100) / 100 :Math.floor(x * 100) / 100 ;
    
    var s_x = f_x.toString();
    var pos_decimal = s_x.indexOf('.');
    if (pos_decimal < 0) {
      pos_decimal = s_x.length;
      s_x += '.';
    }
    while (s_x.length <= pos_decimal + 2) {
      s_x += '0';
    }
    return s_x;
  } catch (e) {
    return '0.00'
  }
}

