export function calculateAge (idCardNum) {
    let age = 0
    if (isNotEmpty(idCardNum) && idCardNum.length==18) {
        let year = Number.parseInt(idCardNum.substr(6, 4))
        let month = Number.parseInt(idCardNum.substr(10, 2))
        let day = Number.parseInt(idCardNum.substr(12, 2))
        let currentDate = new Date()
        let currentYear = currentDate.getFullYear()
        let currenMonth = currentDate.getMonth() + 1 // getMonth 值（0~11）
        let currenDay = currentDate.getDate()
        
        age = currentYear - year
        // if ((currenMonth*100+currenDay) > (Number.parseInt(month)*100+Number.parseInt(day))) {
        //     age += 1
        // }
    }
    return age;
}
// 根据身份证号码计算出生年月
export function bornYear(idCardNum){
    let birth="";
    if(isNotEmpty(idCardNum) && idCardNum.length==18){
        let year=Number.parseInt(idCardNum.substr(6,4));
        let month=Number.parseInt(idCardNum.substr(10,2));
        let day=Number.parseInt(idCardNum.substr(12,2));
        birth=year+"-"+month+"-"+day
    }
    return birth;
}
// 根据身份证号码计算出男女性别
export function getGender(idCardNum){
    let num="",gender="";
    if(isNotEmpty(idCardNum) && idCardNum.length==18){
        num=Number.parseInt(idCardNum.substr(16,1))
        if(num%2==0){
            gender="女"
        }else{
            gender="男"
        }
    }
    return gender;
}
//字符串加密
export function secretPhone(value) {

    let string = value;
    if (isNotEmpty(value) && typeof (value) == 'string' && value.length == 11){
        var reg = /^(\d{3})\d*(\d{4})$/;
        string = value.replace(reg,'$1****$2');
    }

    return string;
}
